import cart from "./cart.json"

export default {
    state: {
      cart: cart,
    },
    mutations: {
      addProductToCart(state, obj) {
        if(state.cart[0].products.length == 0){
            if(obj.Home){
                obj.product.quantity = 1;
                state.cart[0].products.push(obj.product);
                return;
            }
            obj.product.quantity = obj.quantity;
            state.cart[0].products.push(obj.product);
            return;
        }
        for(let p of state.cart[0].products){
            if(p.product_id == obj.product_id){
                p.quantity += obj.quantity;
                return;
            }
        }
        obj.quantity = 1;
        state.cart[0].products.push(obj);
      },
      removeProductFromCart(state, id) {
        state.cart[0].products = state.cart[0].products.filter(x => x.id != id);
      }
    },
    getters: {
    cartProducts(state) {
        return state.cart[0].products;
    },
    totalPrice(state) {
        return state.cart[0].totalPrice;
    }
    }
}
