import products from "../sources/products.json"

export default {
    state: {
      searchObj: { keyword : null, viewpage : 1, perPage: 9 , minPrice : null, maxPrice : null, sort: null ,choosedCategories : [], choosedBrands : [] },
      filteredProducts: products,
      deletedProducts: []
    },
    mutations: {
      changeKeyword(state, keyword) {
        state.searchObj.keyword = keyword
      },
      filterByCategory(state, categories) {
        state.searchObj.choosedCategories = categories
      },
      filterByBrand(state, brands) {
        state.searchObj.choosedBrands = brands
      },
      updateProduct(state, productObj) {
        var obj = state.products.find(x => x.id == productObj.id)
        obj.name = productObj.name
        obj.price = productObj.price
        obj.image = productObj.image
      },
      deleteProduct(state, id) {
        state.deletedProducts.push(id)
      }
    },
    actions: {
      changeKeyword(context, keyword) {
        context.commit('changeKeyword', keyword)
      }
    },
    getters: {
      products(state) {
          state.filteredProducts = state.products;
          if(state.searchObj.keyword) {
            return state.filteredProducts.filter(function(p) {
              return p.name.toLowerCase().includes(state.searchObj.keyword.toLowerCase().trim())
            }) 
          }
          if(state.searchObj.choosedCategories.length > 0) {
            return state.filteredProducts.filter(el => state.searchObj.choosedCategories.includes(el.category_id))
          }
          if(state.searchObj.choosedBrands.length > 0) {
            return state.filteredProducts.filter(el => state.searchObj.choosedBrands.includes(el.brand_id))
          }
          return state.filteredProducts
        },
      nonDeletedProducts(state ,getters) {
        return getters.filteredProducts.filter(x => !state.deletedProducts.includes(x.id))
      }
    }
}
