import Vue from 'vue'
import App from './App.vue'
import VueRouter from 'vue-router'
import Main from './components/pages/Main.vue'
import Store from './components/pages/Store.vue'
import Cart from './components/pages/Cart.vue'
import Product from './components/pages/Product.vue'
import Login from './components/pages/Login.vue'
import Register from './components/pages/Register.vue'
import store from "./store/store.js"
import RoleDirective from "./directives/RoleDirective.js"
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css'

let Script = document.createElement("script");
Script.setAttribute("src", "assets/js/jquery.min.js");
document.head.appendChild(Script); 
// let Script5 = document.createElement("script");
// Script5.setAttribute("src", "assets/js/jquery.zoom.min.js");
// document.head.appendChild(Script5);

let Script3 = document.createElement("script");
Script3.setAttribute("src", "assets/js/slick.min.js");
document.head.appendChild(Script3)
setTimeout(function(){
// let Script4 = document.createElement("script");
// Script4.setAttribute("src", "assets/js/nouislider.min.js");
// document.head.appendChild(Script4);
let Script2 = document.createElement("script");
Script2.setAttribute("src", "assets/js/bootstrap.min.js");
document.head.appendChild(Script2);
let Script6 = document.createElement("script");
Script6.setAttribute("src", "assets/js/mainold.js");
document.querySelector('footer').appendChild(Script6);
}, 50);

Vue.directive("role", RoleDirective)
const instance = axios.create({
  baseURL: "https://itshoppssite.000webhostapp.com/"
})
Vue.prototype.$axios = instance;
Vue.config.productionTip = false

Vue.use(VueRouter)
var routes = [
  { path: "/", component: Main },
  { path: "/store", component: Store },
  { path: "/product/:name", component: Product },
  { path: "/cart", component: Cart},
  { path: "/login", component: Login},
  { path: "/register", component: Register}
]
var router = new VueRouter({routes})
new Vue({
  router,
  store,
  created: function() {
    var user = localStorage.getItem("user")

    if(!user) {
      this.$store.commit("changeUser", undefined)
      return;
    }

    this.$store.commit("changeUser", JSON.parse(user))
  },
  render: h => h(App),
}).$mount('#app')
