import cart from "./cart.json"

export default {
    state: {
      cart: cart,
    },
    mutations: {
      addProductToCart(state, obj) {
        state.cart[0].productspush(obj);
      },
      removeProductFromCart(state, id) {
        state.cart[0].products = state.cart[0].products.filter(x => x.id != id);
      }
    },
    getters: {
    cartProducts(state) {
        return state.cart[0].products;
    },
    totalPrice(state) {
        return state.cart[0].totalPrice;
    }
    }
}
