import products from "../sources/products.json"

export default {
    state: {
      searchObj: { keyword : null, minPrice : null, maxPrice : null, sort: null ,choosedCategories : [], choosedBrands : [] },
      products: products,
      filteredProducts: [],
      deletedProducts: []
    },
    mutations: {
      filterByKeyword(state, keyword) {
        state.searchObj.keyword = keyword
      },
      filterByCategory(state, categories) {
        state.searchObj.choosedCategories = categories
      },
      filterByBrand(state, brands) {
        state.searchObj.choosedBrands = brands
      },
      sortProducts(state, sort) {
        state.searchObj.sort = sort
      },
      changePerPage(state, perPage) {
        state.searchObj.perPage = perPage
      },
      updateProduct(state, productObj) {
        var obj = state.products.find(x => x.id == productObj.id)
        obj.name = productObj.name
        obj.price = productObj.price
        obj.image = productObj.image
      },
      deleteProduct(state, id) {
        state.deletedProducts.push(id)
      }
    },
    // actions: {
    //   filterByKeyword(context, keyword) {
    //     context.commit('filterByKeyword', keyword)
    //   },
    //   filterByCategory(context, categories) {
    //     context.commit('filterByCategory', categories)
    //   },
    //   filterByBrand(context, brands) {
    //     context.commit('filterByBrand', brands)
    //   }
    // },
    getters: {
      products(state) {
          let oldpr = [];
          oldpr = state.products
          if(state.searchObj.keyword) {
            state.products = state.products.filter(function(p) {
              return p.name.toLowerCase().includes(state.searchObj.keyword.toLowerCase().trim())
            }) 
          }
          if(state.searchObj.choosedCategories.length > 0) {
            state.products = state.products.filter(el => state.searchObj.choosedCategories.includes(el.category_id))
          }
          if(state.searchObj.choosedBrands.length > 0) {
            state.products = state.products.filter(el => state.searchObj.choosedBrands.includes(el.brand_id))
          }
          if(state.searchObj.sort) {
            state.products = state.products.sort((a, b) => {
              if(state.searchObj.sort == 'name-asc') {
                return a.name.localeCompare(b.name)
              }
              else if(state.searchObj.sort == 'name-desc') {
                return b.name.localeCompare(a.name)
              }
              else if(state.searchObj.sort == 'price-asc') {
                return a.price - b.price
              }
              else if(state.searchObj.sort == 'price-desc') {
                return b.price - a.price
              }
              else if(state.searchObj.sort == 'newest') {
                return b.product_id - a.product_id
              }
            } )
          }
          if(state.products.length == 0 && (state.searchObj.keyword || state.searchObj.choosedCategories.length > 0 || state.searchObj.choosedBrands.length > 0 || state.searchObj.sort || state.searchObj.minPrice || state.searchObj.maxPrice)) {
            return "No products found";
          }
          else if (!(state.searchObj.keyword || state.searchObj.choosedCategories.length > 0 || state.searchObj.choosedBrands.length > 0 || state.searchObj.sort || state.searchObj.minPrice || state.searchObj.maxPrice)){
            return oldpr;
          }
          else {
            return state.products;
          }
        },
      // nonDeletedProducts(state ,getters) {
      //   return getters.products.filter(x => !state.deletedProducts.includes(x.id))
      // }
    }
}
