import users from "./users.json"
export default {
    state: {
      user: { username : "guest", password: "" ,role: "unauthorized" },
      users : users
    },
    mutations: {
        changeUsername(state, newUsername) {
            state.user.username = newUsername;
        },
        registerUser(state, newUser) {
            state.user.username = newUser.username,
            state.user.password = newUser.password,
            state.user.role = newUser.role
        },
        logoutUser(state) {
            state.user.username = "guest";
            state.user.password = "";
            state.user.role = "unauthorized";
            localStorage.removeItem("user");
        }
    },
    getters: {
        username(state) {
            return state.user.username;
        },
        isAdmin(state) {
            return state.user.isAdmin;
        },
        role(state) {
            return state.user.role;
        },
        users(state) {
            return state.users;
        }
    }
}
